import React, { useState, useEffect } from 'react';
import { FileText, Mail, BookOpen, Users, Brain, Database, Dna, Sun, Moon } from 'lucide-react';
import { Alert, AlertDescription } from './components/ui/alert';

import AcademicLinks from './components/AcademicLinks';
import PublicationList from './components/PublicationList';
import ProjectList from './components/ProjectList';

const PersonalPage = () => {
  // const [setCopiedIndex] = useState(null);
  const [darkMode, setDarkMode] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Update HTML class when dark mode changes
  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
      document.body.style.backgroundColor = '#1a1a1a';
    } else {
      document.documentElement.classList.remove('dark');
      document.body.style.backgroundColor = '#ffffff';
    }
  }, [darkMode]);

  return (
    <div className={`min-h-screen ${darkMode ? 'dark bg-gray-900' : 'bg-gray-50'}`}>
      {/* Mobile Menu Button - Only shows on mobile */}
      <div className="md:hidden fixed top-4 left-4 z-50">
        <button
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          className="p-3 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200"
        >
          {isMenuOpen ? '✕' : '☰'}
        </button>
      </div>

      {/* Theme Toggle */}
      <button
        onClick={() => setDarkMode(!darkMode)}
        className="fixed top-4 right-4 p-3 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200 z-50"
      >
        {darkMode ? <Sun className="w-6 h-6" /> : <Moon className="w-6 h-6" />}
      </button>

      {/* Main Content Container */}
      <div className="container mx-auto px-4 py-8">
        <div className="grid grid-cols-1 md:grid-cols-5 gap-8">
            {/* Left Column - Profile Info */}
            <div className={`
            md:col-span-1 
            ${isMenuOpen ? 'fixed inset-0 bg-white dark:bg-gray-800 z-40 overflow-y-auto p-4' : 'hidden md:block'}
          `}>
              <div className="sticky top-8 space-y-6 bg-white dark:bg-gray-800 p-6 rounded-lg shadow-sm">
              <div className="text-center">
                <img 
                  src="/api/placeholder/150/150"
                  alt="Profile"
                  className="w-32 h-32 mx-auto rounded-full mb-4"
                />
                <h1 className="text-2xl font-bold text-gray-900 dark:text-white text-center mb-2">
                  Your Name
                </h1>
                <p className="text-lg text-gray-600 dark:text-gray-300 text-center">
                  Professor of [Your Field]
                </p>
                <p className="text-gray-600 dark:text-gray-400 text-center mb-4">
                  [University Name]
                </p>
                </div>
                <div className="flex-shrink-0">
                <AcademicLinks />
              </div>
                  {/* Contact Info */}
                <div className="space-y-4 text-sm md:text-base">
                  <div>
                    <h2 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                      Contact
                    </h2>
                    <p className="text-gray-600 dark:text-gray-300">
                <Mail className="w-4 h-4 inline mr-2" />
                matilde.pato@isel.pt
              </p>
              <p className="text-gray-600 dark:text-gray-300">
                <BookOpen className="w-4 h-4 inline mr-2" />
                DEETC
              </p>
              <p className="text-gray-600 dark:text-gray-300">
                <FileText className="w-4 h-4 inline mr-2" />
                Office: Building F, Room F0.15
              </p>
                  </div>
              </div>
                  <div>
                    <h2 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                      Research Interests
                    </h2>
                    <ul className="list-disc pl-5 text-gray-600 dark:text-gray-300">
                      <li>Research Area 1</li>
                      <li>Research Area 2</li>
                      <li>Research Area 3</li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>

            <div className="md:col-span-4">
            {/* Right Column */}
          <div className="md:col-span-2 space-y-8">
            {/* Research Opportunities Alert */}
            <Alert className="bg-blue-50 dark:bg-blue-900/30 border-blue-200 dark:border-blue-800">
            <Users className="w-5 h-5 text-blue-600 dark:text-blue-400" />
            <AlertDescription className="ml-2">
              <h3 className="text-lg font-semibold text-blue-800 dark:text-blue-300 mb-2">
                Looking for Research Collaborators!
              </h3>
              <p className="text-blue-700 dark:text-blue-300">
                I'm actively seeking passionate students and researchers interested in:
              </p>
             <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-4 text-sm md:text-base">
                <div className="flex items-center space-x-2 text-blue-700 dark:text-blue-300">
                  <Brain className="w-4 h-4" />
                  <span>Machine Learning</span>
                </div>
                <div className="flex items-center space-x-2 text-blue-700 dark:text-blue-300">
                  <Database className="w-4 h-4" />
                  <span>Recommender Systems</span>
                </div>
                <div className="flex items-center space-x-2 text-blue-700 dark:text-blue-300">
                  <Dna className="w-4 h-4" />
                  <span>Bioinformatics</span>
                </div>
              </div> 

              <p className="mt-4 text-blue-700 dark:text-blue-300">
                If you're interested in pursuing research in these areas, please reach out via email 
                to discuss potential collaboration opportunities, including PhD positions and research internships.
              </p>
            </AlertDescription>
          </Alert>

            {/* Publications */}
            {/* <div className="bg-white dark:bg-gray-800 p-4 md:p-6 rounded-lg shadow-sm">
                <PublicationList />
              </div> */}

              {/* Projects */}
              {/* <div className="bg-white dark:bg-gray-800 p-4 md:p-6 rounded-lg shadow-sm">
                <ProjectList />
              </div> */}
          </div>
        </div>
      </div>
    </div>
  );
};


export default PersonalPage;
