// src/components/teaching/StudentsList.jsx
import React from 'react';

const StudentsList = () => {
  const students = {
    current: [
      {
        name: "Student Name 1",
        level: "PhD",
        topic: "Machine Learning in Healthcare",
        year: "2023-present",
        institution: "FCUL"
      },
      // Add more current students
    ],
    past: [
      {
        name: "Student Name 2",
        level: "MSc",
        topic: "Recommender Systems",
        year: "2021-2023",
        institution: "ISEL",
        thesis: "Thesis Title Here",
      },
      // Add more past students
    ]
  };

  return (
    <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-sm">
      <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">Students</h2>

      {/* Current Students */}
      <div className="mb-6">
        <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-3">
          Current Students
        </h3>
        <div className="space-y-4">
          {students.current.map((student, index) => (
            <div key={index} className="border-l-4 border-blue-500 pl-4">
              <p className="font-semibold text-gray-900 dark:text-white">
                {student.name}
              </p>
              <p className="text-gray-600 dark:text-gray-300">
                {student.level} Student • {student.institution}
              </p>
              <p className="text-gray-500 dark:text-gray-400">
                {student.topic} ({student.year})
              </p>
            </div>
          ))}
        </div>
      </div>

      {/* Past Students */}
      <div>
        <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-3">
          Past Students
        </h3>
        <div className="space-y-4">
          {students.past.map((student, index) => (
            <div key={index} className="border-l-4 border-green-500 pl-4">
              <p className="font-semibold text-gray-900 dark:text-white">
                {student.name}
              </p>
              <p className="text-gray-600 dark:text-gray-300">
                {student.level} • {student.institution} • {student.year}
              </p>
              <p className="text-gray-500 dark:text-gray-400">
                {student.thesis}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default StudentsList;
