import React, { useState, useEffect } from 'react';
import { FileText, Link, Mail, BookOpen, Copy, CheckCheck, Users, Brain, Database, Dna, Sun, Moon } from 'lucide-react';
import { Alert, AlertDescription } from './components/ui/alert';
import './assets/css/academicons.css';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
// import { 
//   faOrcid,
//   faGoogleScholar,
//   faResearchgate,
//   faCienciaVitae
// } from '@fortawesome/free-brands-svg-icons';

const PersonalPage = () => {
  const [copiedIndex, setCopiedIndex] = useState(null);
  const [darkMode, setDarkMode] = useState(false);

  // Update HTML class when dark mode changes
  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
      document.body.style.backgroundColor = '#1a1a1a';
    } else {
      document.documentElement.classList.remove('dark');
      document.body.style.backgroundColor = '#ffffff';
    }
  }, [darkMode]);

  const publications = [
    {
      title: "Publication Title 1",
      authors: "Author1, Author2, Your Name",
      journal: "Journal Name",
      year: "2024",
      doi: "10.1234/example",
      bibtex: `@article{paper2024,
        title={Publication Title 1},
        author={Author1 and Author2 and Your Name},
        journal={Journal Name},
        year={2024},
        doi={10.1234/example}
      }`
    },
    {
      title: "Publication Title 2",
      authors: "Your Name, Author3",
      journal: "Conference Proceedings",
      year: "2023",
      doi: "10.1234/example2",
      bibtex: `@inproceedings{conf2023,
        title={Publication Title 2},
        author={Your Name and Author3},
        booktitle={Conference Proceedings},
        year={2023},
        doi={10.1234/example2}
      }`
    }
  ];

  const copyBibtex = (bibtex, index) => {
    navigator.clipboard.writeText(bibtex);
    setCopiedIndex(index);
    setTimeout(() => setCopiedIndex(null), 2000);
  };

  const AcademicLinks = () => {
    return (
      <div className="flex justify-center space-x-6 mt-6">
        <a href="http://orcid.org/0000-0001-8976-7651" 
           className="text-blue-600 hover:text-blue-800 flex items-center">
          <i className="ai ai-orcid ai-2x"></i>
          <span className="ml-2">0000-0001-8976-7651</span>
        </a>
        <a href="https://www.cienciavitae.pt/portal/FA11-8E2D-DC05" 
           className="text-blue-600 hover:text-blue-800 flex items-center">
          <i className="ai ai-cienciavitae ai-2x"></i>
          <span className="ml-2">FA11-8E2D-DC05</span>
        </a>
        <a href="http://scholar.google.pt/citations?user=xtCBV-wAAAAJ&hl=pt-PT" className="text-blue-600 hover:text-blue-800 flex items-center">
          <i className="ai ai-google-scholar ai-2x"></i>
          <span className="ml-2">Google Scholar</span>
        </a>
        <a href="https://www.researchgate.net/profile/Matilde_Pato" className="text-blue-600 hover:text-blue-800 flex items-center">
          <i className="ai ai-researchgate ai-2x"></i>
          <span className="ml-2">ResearchGate</span>
        </a>
        <a href="https://www.scopus.com/authid/detail.uri?authorId=16550896700" className="text-blue-600 hover:text-blue-800 flex items-center">
          <i className="ai ai-scopus ai-2x"></i>
          <span className="ml-2">16550896700</span>
        </a>
        <a href="https://www.webofscience.com/wos/author/record/AAM-8381-2020" className="text-blue-600 hover:text-blue-800 flex items-center">
          <i className="ai ai-researcherid ai-2x"></i>
          <span className="ml-2">AAM-8381-2020</span>
        </a>
        <a href="http://pt.linkedin.com/in/matildepato" className="text-blue-600 hover:text-blue-800 flex items-center">
          <i className="ai ai-linkedin ai-2x"></i>
          <span className="ml-2">Scopus</span>
        </a>
      </div>
    );
  };

  return (
    <div className={`min-h-screen ${darkMode ? 'dark bg-gray-900' : 'bg-gray-50'}`}>
      {/* Theme Toggle Button - Fixed position */}
      <button
        onClick={() => setDarkMode(!darkMode)}
        className="fixed top-4 right-4 p-3 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200 hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200 z-50"
      >
        {darkMode ? <Sun size={20} /> : <Moon size={20} />}
      </button>

      {/* Main Content Container */}
      <div className="max-w-4xl mx-auto px-4 py-8">
        {/* Header Section */}
        <header className="text-center mb-12">
          <img 
            src="/api/placeholder/150/150"
            alt="Profile"
            className="w-32 h-32 mx-auto rounded-full mb-6"
          />
          <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-2">Your Name</h1>
          <p className="text-xl text-gray-600 dark:text-gray-300">Professor of [Your Field]</p>
          <p className="text-gray-600 dark:text-gray-400">[University Name]</p>

          {/* Academic Profile Links */}
          <div className="flex justify-center space-x-6 mt-6">
            {/* Academic Profile Links */}
            <div className="flex justify-center space-x-6 mt-6">
            <a href="https://orcid.org/0000-0001-8976-7651" className="text-blue-600 dark:text-blue-400 hover:text-blue-800 dark:hover:text-blue-300 flex items-center">
              <FontAwesomeIcon icon={faOrcid} alt="ORCID" className="w-5 h-5 mr-1" />
              ORCID
            </a>
            <a href="http://scholar.google.pt/citations?user=xtCBV-wAAAAJ&hl=pt-PT" className="text-blue-600 dark:text-blue-400 hover:text-blue-800 dark:hover:text-blue-300 flex items-center">
              <FontAwesomeIcon icon={faGoogleScholar} alt="Google Scholar" className="w-5 h-5 mr-1" />
              Google Scholar
            </a>
            <a href="http://www.researchgate.net/profile/Matilde_Pato" className="text-blue-600 dark:text-blue-400 hover:text-blue-800 dark:hover:text-blue-300 flex items-center">
              <FontAwesomeIcon icon={faResearchgate} alt="ResearchGate" className="w-5 h-5 mr-1" />
              ResearchGate
            </a>
            <a href="https://www.cienciavitae.pt/portal/FA11-8E2D-DC05" className="text-blue-600 dark:text-blue-400 hover:text-blue-800 dark:hover:text-blue-300 flex items-center">
              <FontAwesomeIcon icon={faCienciaVitae} alt="CienciaVitae" className="w-5 h-5 mr-1" />
              ResearchGate
            </a>
          </div>
        </div>
      </header>

      {/* Main Content Sections */}
      <main className="space-y-12">
        {/* About Section */}
        <section>
            <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">About Me</h2>
            <p className="space-y-4">
              Your academic background and research interests.
            </p>
          </section>

        {/* Research Opportunities Alert */}
        <section className="mb-12">
          <Alert className="bg-blue-50 dark:bg-blue-900/30 border-blue-200 dark:border-blue-800">
            <Users className="w-5 h-5 text-blue-600 dark:text-blue-400" />
            <AlertDescription className="ml-2">
              <h3 className="text-lg font-semibold text-blue-800 dark:text-blue-300 mb-2">
                Looking for Research Collaborators!
              </h3>
              <p className="text-blue-700 dark:text-blue-300">
                I'm actively seeking passionate students and researchers interested in:
              </p>
              <div className="mt-4 grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="flex items-center space-x-2 text-blue-700 dark:text-blue-300">
                  <Brain className="w-4 h-4" />
                  <span>Machine Learning</span>
                </div>
                <div className="flex items-center space-x-2 text-blue-700 dark:text-blue-300">
                  <Database className="w-4 h-4" />
                  <span>Recommender Systems</span>
                </div>
                <div className="flex items-center space-x-2 text-blue-700 dark:text-blue-300">
                  <Dna className="w-4 h-4" />
                  <span>Bioinformatics</span>
                </div>
              </div>
              <p className="mt-4 text-blue-700 dark:text-blue-300">
                If you're interested in pursuing research in these areas, please reach out via email 
                to discuss potential collaboration opportunities, including PhD positions and research internships.
              </p>
            </AlertDescription>
          </Alert>
        </section>

        {/* Publications Section */}
        <section className="mb-12">
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">Selected Publications</h2>
          <div className="space-y-6">
            {publications.map((pub, index) => (
              <div key={index} className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-sm transition-colors duration-200">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">{pub.title}</h3>
                <p className="text-gray-600 dark:text-gray-300 mt-2">{pub.authors}</p>
                <p className="text-gray-500 dark:text-gray-400 mt-1">
                  {pub.journal} ({pub.year})
                </p>
                <div className="flex items-center mt-2">
                  <Link className="w-4 h-4 mr-1" />
                  <a href={`https://doi.org/${pub.doi}`} 
                     className="text-blue-600 dark:text-blue-400 hover:text-blue-800 dark:hover:text-blue-300">
                    DOI: {pub.doi}
                  </a>
                </div>
                <div className="mt-3 flex items-center">
                  <button
                    onClick={() => copyBibtex(pub.bibtex, index)}
                    className="inline-flex items-center text-sm text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white bg-gray-100 dark:bg-gray-700 hover:bg-gray-200 dark:hover:bg-gray-600 px-3 py-1 rounded-md transition-colors duration-200"
                  >
                    {copiedIndex === index ? (
                      <>
                        <CheckCheck className="w-4 h-4 mr-1" />
                        Copied BibTeX
                      </>
                    ) : (
                      <>
                        <Copy className="w-4 h-4 mr-1" />
                        Copy BibTeX
                      </>
                    )}
                  </button>
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* Teaching Section */}
        <section className="mb-12">
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">Teaching</h2>
          <div className="space-y-4">
            {[
              {
                course: "Course Title 1",
                code: "ABC123",
                level: "Undergraduate",
                description: "Brief description of the course content and objectives"
              },
              {
                course: "Course Title 2",
                code: "XYZ789",
                level: "Graduate",
                description: "Brief description of the course content and objectives"
              }
            ].map((course, index) => (
              <div key={index} className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-sm transition-colors duration-200">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                  {course.course} ({course.code})
                </h3>
                <p className="text-gray-600 dark:text-gray-300 mt-1">{course.level}</p>
                <p className="text-gray-500 dark:text-gray-400 mt-2">{course.description}</p>
              </div>
            ))}
          </div>
        </section>

        {/* Contact Section */}
        <section>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">Contact</h2>
          <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-sm transition-colors duration-200">
            <div className="space-y-2">
              <p className="text-gray-600 dark:text-gray-300">
                <Mail className="w-4 h-4 inline mr-2" />
                Email: your.email@university.edu
              </p>
              <p className="text-gray-600 dark:text-gray-300">
                <BookOpen className="w-4 h-4 inline mr-2" />
                Department of [Your Department]
              </p>
              <p className="text-gray-600 dark:text-gray-300">
                <FileText className="w-4 h-4 inline mr-2" />
                Office: Building Name, Room XXX
              </p>
            </div>
          </div>
        </section>
      </main>

      <footer className="bg-white dark:bg-gray-800 mt-12 transition-colors duration-200">
        <div className="max-w-4xl mx-auto py-6 px-4 text-center text-gray-600 dark:text-gray-400">
          <p>Â© 2025 Your Name. All rights reserved.</p>
        </div>
      </footer>
    </div>
    </div>
  );
};

export default PersonalPage;
